"use strict";
function floatchart() {
    var e = {
            legend: { show: !1 },
            series: { label: "", curvedLines: { active: !0, nrSplinePoints: 20 } },
            tooltip: { show: !0, content: "x : %x | y : %y" },
            grid: { hoverable: !0, borderWidth: 0, labelMargin: 0, axisMargin: 0, minBorderMargin: 0 },
            yaxis: { min: 0, max: 30, color: "transparent", font: { size: 0 } },
            xaxis: { color: "transparent", font: { size: 0 } },
        },
        a = {
            legend: { show: !1 },
            series: { label: "", curvedLines: { active: !0, nrSplinePoints: 20 } },
            tooltip: { show: !0, content: "x : %x | y : %y" },
            grid: { hoverable: !0, borderWidth: 0, labelMargin: 0, axisMargin: 0, minBorderMargin: 8 },
            yaxis: { min: 0, max: 30, color: "transparent", font: { size: 0 } },
            xaxis: { color: "transparent", font: { size: 0 } },
        };
    $(function () {
        $.plot(
            $("#sec-ecommerce-chart-line"),
            [
                {
                    data: [
                        [0, 18],
                        [1, 10],
                        [2, 20],
                        [3, 10],
                        [4, 27],
                        [5, 15],
                        [6, 20],
                        [7, 28],
                        [8, 20],
                        [9, 10],
                        [10, 18],
                        [11, 10],
                        [12, 20],
                        [13, 10],
                        [14, 27],
                    ],
                    color: "#fff",
                    lines: { show: !0, fill: !1, lineWidth: 2 },
                    points: { show: !0, radius: 3, fill: !0, fillColor: "#fff" },
                    curvedLines: { apply: !1 },
                },
            ],
            a
        ),
            $.plot(
                $("#sec-ecommerce-chart-bar"),
                [
                    {
                        data: [
                            [0, 18],
                            [1, 10],
                            [2, 20],
                            [3, 10],
                            [4, 27],
                            [5, 15],
                            [6, 20],
                            [7, 24],
                            [8, 20],
                            [9, 16],
                            [10, 18],
                            [11, 10],
                            [12, 20],
                            [13, 10],
                            [14, 27],
                        ],
                        color: "#add3ff",
                        bars: { show: !0, lineWidth: 1, fill: !0, fillColor: { colors: [{ opacity: 1 }, { opacity: 1 }] }, barWidth: 0.6, align: "center", horizontal: !1 },
                        points: { show: !1 },
                    },
                ],
                e
            );
    });
}
$(document).ready(function () {
    floatchart(),
    $(window).on("resize", function () {
        floatchart();
    }),
    $("#mobile-collapse").on("click", function () {
        setTimeout(function () {
            floatchart();
        }, 700);
    }),
    $(".scroll-widget").slimScroll({ size: "5px", height: "290px", allowPageScroll: !1 });
    var dataHls
    $.ajax({
        type : 'get',
        url : 'hls/get-hls',
        async : false,
        success : function(data){
            dataHls = data
        }
    })
    dataHls = JSON.parse(dataHls)

    var dataRls
    $.ajax({
        type : 'get',
        url : 'rls/get-rls',
        async : false,
        success : function(data){
            dataRls = data
        }
    })
    dataRls = JSON.parse(dataRls)

    Highcharts.chart('container-hls', {
        chart: {
            type: 'areaspline'
        },
        title: {
            text: 'Grafik HLS Tiap Tahun'
        },
        xAxis: {
            categories: dataHls.tahun,
        },
        yAxis: {
            title: {
                text: 'HLS'
            }
        },
        tooltip: {
            shared: true,
        },
        credits: {
            enabled: false
        },
        plotOptions: {
            areaspline: {
                fillOpacity: 0.5
            }
        },
        series: [{
            name : 'HLS',
            data: dataHls.hls
        }]
    });  
    Highcharts.chart('container-rls', {
        chart: {
            type: 'areaspline'
        },
        title: {
            text: 'Grafik RLS Tiap Tahun'
        },
        xAxis: {
            categories: dataRls.tahun,
        },
        yAxis: {
            title: {
                text: 'RLS'
            }
        },
        tooltip: {
            shared: true,
        },
        credits: {
            enabled: false
        },
        plotOptions: {
            areaspline: {
                fillOpacity: 0.5
            }
        },
        series: [{
            name : 'RLS',
            data: dataRls.rls
        }]
    });    
    setTimeout(function () {
        e.zoomToIndexes(Math.round(0.45 * e.dataProvider.length), Math.round(0.6 * e.dataProvider.length));
    }, 800);
});
